(9, 12, 'LIVEOPENCART: Parent-child Options', 'parent-child-options', 'LIVEOPENCART (support@liveopencart.com)', '3.0.3b', 'http://liveopencart.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n	<code>parent-child-options</code>\r\n  <name>LIVEOPENCART: Parent-child Options</name>\r\n  <version>3.0.3b</version>\r\n  <author>LIVEOPENCART (support@liveopencart.com)</author>\r\n  <link>http://liveopencart.com</link>\r\n  \r\n  <file path=\"admin/controller/catalog/product.php\">\r\n		\r\n		<!--\r\n		<operation>\r\n      <search><![CDATA[\r\n				$this->load->language(\'catalog/product\');\r\n			]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n        // << Parent-child Options\r\n				$this->load->language(\'extension/module/parent_child_options\');\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n		-->\r\n    \r\n    <operation>\r\n      <search>\r\n        <![CDATA[function getForm() {]]>\r\n      </search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n        \r\n				$this->load->model(\'extension/module/parent_child_options\');\r\n				if ( $this->model_extension_module_parent_child_options->installed() ) {\r\n				\r\n					$this->document->addScript($this->model_extension_module_parent_child_options->getProductEditPageScriptPath());\r\n					\r\n					$data = array_merge( !empty($data) ? $data : array(), $this->model_extension_module_parent_child_options->getProductPageData() );\r\n				\r\n        }\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation>\r\n      <search>\r\n        <![CDATA[$data[\'product_options\'][] = array(]]>\r\n      </search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n        \'pcop\'    => isset($product_option[\'pcop\']) ? $product_option[\'pcop\'] : false,\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n		\r\n		<operation>\r\n      <search><![CDATA[\r\n				$option_data[] = array(\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n				\'pcop_front\'    => isset($product_option[\'pcop_front\']) ? $product_option[\'pcop_front\'] : false,\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n  </file>\r\n  \r\n  <file path=\"admin/model/catalog/product.php\">\r\n    \r\n    <operation>\r\n      <search><![CDATA[\r\n				function getProductOptions($product_id\r\n			]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n        \r\n        // << Parent-child Options\r\n        \r\n        public function pcop_checkTables() {\r\n          $this->load->model(\'extension/module/parent_child_options\');\r\n					$this->model_extension_module_parent_child_options->checkTables();\r\n        }\r\n        \r\n        public function pcop_getParents($product_option_id) {\r\n          $this->load->model(\'extension/module/parent_child_options\');\r\n					return $this->model_extension_module_parent_child_options->getProductOptionParents($product_option_id);\r\n        }\r\n              \r\n        // >> Parent-child Options\r\n        \r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation>\r\n      <search><![CDATA[\r\n				$product_option_data[] = array(\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n        \'pcop\' => $this->pcop_getParents($product_option[\'product_option_id\']),\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n		<!--\r\n			some specific modiles update product options (insert) by themselves (like PSO) and add code by vQmod\r\n			it is not possible to get product option ids in this case at the first option saving,\r\n			but only on next savings, when product option ids are already known \r\n		-->\r\n		<operation>\r\n      <search><![CDATA[\r\n				foreach ($data[\'product_option\'] as $product_option) {\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n				\r\n				if (__FUNCTION__ == \'addProduct\' || __FUNCTION__ == \'editProduct\') {\r\n					if ( !empty($product_option[\'product_option_id\']) ) {\r\n						$pcop_product_option_id = $product_option[\'product_option_id\'];\r\n						if ( isset($product_option[\'product_option_temp_id\']) ) {\r\n							$pcop_temp_ids[\'po\'][$product_option[\'product_option_temp_id\']] = $pcop_product_option_id;\r\n						}\r\n					}\r\n				}\r\n        \r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation>\r\n      <search><![CDATA[\r\n				$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_option SET\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n        \r\n        if ( !empty($product_option) ) {\r\n          $pcop_product_option_id = $this->db->getLastId();\r\n					if ( isset($product_option[\'product_option_temp_id\']) ) {\r\n						$pcop_temp_ids[\'po\'][$product_option[\'product_option_temp_id\']] = $pcop_product_option_id;\r\n					} elseif ( isset($product_option[\'product_option_id\']) ) {\r\n						$pcop_temp_ids[\'po\'][$product_option[\'product_option_id\']] = $pcop_product_option_id;\r\n					}\r\n        } \r\n        \r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n		\r\n		<operation>\r\n      <search><![CDATA[\r\n				$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_option_value\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n        \r\n        if ( !empty($product_option_value) ) {\r\n          $pcop_product_option_value_id = $this->db->getLastId();\r\n					if ( isset($product_option_value[\'product_option_value_temp_id\']) ) {\r\n						$pcop_temp_ids[\'pov\'][$product_option_value[\'product_option_value_temp_id\']] = $pcop_product_option_value_id;\r\n					} elseif ( isset($product_option_value[\'product_option_value_id\']) ) {\r\n						$pcop_temp_ids[\'pov\'][$product_option_value[\'product_option_value_id\']] = $pcop_product_option_value_id;\r\n					}\r\n        } \r\n        \r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n		\r\n		\r\n		<operation>\r\n      <search><![CDATA[\r\n				$this->cache->delete(\'product\');\r\n			]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n        // << Parent-child Options\r\n        \r\n        if ( (__FUNCTION__ == \'addProduct\' || __FUNCTION__ == \'editProduct\') && isset($data[\'product_option\']) ) {\r\n					$this->load->model(\'extension/module/parent_child_options\');					\r\n					$this->model_extension_module_parent_child_options->setProductOptionsParents($product_id, $data[\'product_option\'], $pcop_temp_ids);\r\n				} elseif ( __FUNCTION__ == \'deleteProduct\' ) {\r\n					$this->load->model(\'extension/module/parent_child_options\');					\r\n					$this->model_extension_module_parent_child_options->removeProductPCOP($product_id);\r\n				}\r\n        \r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n		\r\n    \r\n  </file>\r\n  \r\n  <file path=\"admin/view/template/catalog/product_form.twig\">\r\n    \r\n    <operation>\r\n      <search><![CDATA[\r\n				{{ footer }}\r\n			]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n        <!-- << Parent-child Options -->\r\n				{% if ( pcop_installed ?? 0 ) %}\r\n					<script type=\"text/javascript\"><!--\r\n					\r\n						pcop.all_product_options = {{ (pcop_all_product_options ?? 0) ? pcop_all_product_options|json_encode : \'false\' }};\r\n						pcop.basic_product_options = {{ product_options is defined ? product_options|json_encode : \'{}\' }};\r\n						pcop.texts = {{ pcop_texts is defined ? pcop_texts|json_encode : \'{}\' }};\r\n						pcop.init( {{ option_row }} );\r\n						\r\n						{#\r\n						{% for i_option_row in 0..option_row %}\r\n							pcop.showOptionSettings( {{ i_option_row }} );\r\n						{% endfor %}\r\n						#}\r\n						\r\n					//--></script>\r\n				{% endif %}\r\n        <!-- << Parent-child Options -->\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    \r\n    <operation>\r\n      <search><![CDATA[\r\n				$(\'#tab-option .tab-content\').append(html);\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// << Parent-child Options\r\n				{% if ( pcop_installed ?? 0 ) %}\r\n					pcop.showOptionSettings(option_row);\r\n				{% endif %}	\r\n				// >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n		\r\n		<operation error=\"skip\">\r\n      <search><![CDATA[\r\n				<input type=\"hidden\" name=\"product_option[{{ option_row }}][product_option_value][{{ option_value_row }}][product_option_value_id]\" value=\"{{ product_option_value.product_option_value_id }}\" />\r\n			]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n				{# // << Parent-child Options #}\r\n				{% if ( (pcop_installed ?? false) and (product_option_value.product_option_value_temp_id ?? false) ) %}\r\n					<input type=\"hidden\" name=\"product_option[{{ option_row }}][product_option_value][{{ option_value_row }}][product_option_value_temp_id]\" value=\"{{ product_option_value.product_option_value_temp_id }}\" />\r\n				{% endif %}	\r\n				{# // >> Parent-child Options #}\r\n      ]]></add>\r\n    </operation>\r\n		\r\n  </file>\r\n  \r\n  <file path=\"catalog/controller/product/product.php\"> \r\n    \r\n		<operation>\r\n      <search><![CDATA[\r\n				public function index() {\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n				\r\n				if ( !$this->parent_child_options_common ) {\r\n					$this->load->library(\'liveopencart/parent_child_options_common\');\r\n				}\r\n				if ( $this->parent_child_options_common->installed() ) {\r\n					$this->document->addScript($this->parent_child_options_common->getProductPageScriptPath());\r\n				}\r\n        \r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n		\r\n    <operation>\r\n      <search><![CDATA[\r\n				foreach ($this->model_catalog_product->getProductOptions(\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n        if ( !isset($pcop_data) ) $pcop_data = array();\r\n        $pcop_data[$option[\'product_option_id\']] = isset($option[\'pcop_front\']) ? $option[\'pcop_front\'] : array();\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation>\r\n      <search><![CDATA[\r\n				$data[\'reviews\']\r\n			]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n        // << Parent-child Options\r\n        $data[\'pcop_data\'] = !empty($pcop_data) ? $pcop_data : array();\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation>\r\n      <search>\r\n        <![CDATA[$data[\'product_id\'] =]]>\r\n      </search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n				if ( !$this->parent_child_options_common ) {\r\n					$this->load->library(\'liveopencart/parent_child_options_common\');\r\n				}\r\n        $data[\'pcop_theme_name\'] = $this->parent_child_options_common->getThemeName();\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n  </file>\r\n  \r\n  <file path=\"catalog/model/catalog/product.php|admin/model/catalog/product.php\">\r\n    <operation>\r\n      <search><![CDATA[\r\n				public function getProduct($product_id) {\r\n			]]> </search>\r\n      <add position=\"before\"><![CDATA[\r\n        // << Parent-child Options\r\n        function pcop_front_getProductOptionParents($product_option_id) {\r\n					if ( !$this->parent_child_options_common ) {\r\n						$this->load->library(\'liveopencart/parent_child_options_common\');\r\n					}\r\n					return $this->parent_child_options_common->getProductOptionFrontParents($product_option_id);\r\n				}\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation>\r\n      <search><![CDATA[\r\n				\'product_option_id\'    => $product_option[\'product_option_id\'],\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n        \'pcop_front\'    => $this->pcop_front_getProductOptionParents($product_option[\'product_option_id\']),\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n  </file>\r\n	\r\n	<!--\r\n	<file path=\"catalog/view/theme/*/template/product/product.twig|admin/view/template/sale/order_form.twig\">\r\n	-->\r\n	<file path=\"catalog/view/theme/*/template/product/product.twig\">\r\n    <operation>\r\n      <search regex=\"true\"><![CDATA[~({{[ ]*footer[ ]*}})~]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n        {% if ( pcop_data ?? false ) %}\r\n					<!-- << Parent-child Options -->\r\n					<script type=\"text/javascript\"><!--\r\n					\r\n						var pcop_data = {{ (pcop_data ?? 0) ? pcop_data|json_encode : \'[]\' }};\r\n						var pcop_theme_name = \'{{ pcop_theme_name ?? \'\' }}\';\r\n						\r\n						if ( typeof(pcop_front) != \'undefined\' ) {\r\n							pcop_front.init(pcop_data, pcop_theme_name);\r\n						}\r\n					\r\n					//--></script>\r\n					<!-- >> Parent-child Options -->\r\n        {% endif %}\r\n				$1\r\n      ]]></add>\r\n    </operation>\r\n		\r\n  </file>\r\n	\r\n	<file path=\"admin/controller/sale/order.php\">\r\n    \r\n		<operation>\r\n      <search><![CDATA[\r\n				public function getForm(\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n				if ( !$this->parent_child_options_common ) {\r\n					$this->load->library(\'liveopencart/parent_child_options_common\');\r\n				}\r\n				if ( $this->parent_child_options_common->installed() ) {\r\n					$this->document->addScript($this->parent_child_options_common->getProductPageScriptPath());\r\n				}\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n		\r\n  </file>\r\n	\r\n	<file path=\"admin/view/template/sale/order_form.twig\">\r\n    \r\n		<operation>\r\n      <search><![CDATA[\r\n				$(\'#option\').html(\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        // << Parent-child Options\r\n				if ( typeof(pcop_front) != \'undefined\' ) {\r\n					pcop_front.init();\r\n					pcop_front.updateDefaultValues();\r\n					pcop_front.updateData(item[\'option\']);\r\n					pcop_front.checkVisibility();\r\n				}\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n		\r\n  </file>\r\n  \r\n  <file path=\"catalog/controller/checkout/cart.php|catalog/controller/api/cart.php|catalog/controller/extension/module/quotation.php\">\r\n    <operation>\r\n      <search><![CDATA[\r\n				$product_options = $this->model_catalog_product->getProductOptions(\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n        \r\n        // << Parent-child Options\r\n        if (isset($this->request->post[\'options_pcop_not_required\']) && $this->request->post[\'options_pcop_not_required\']) {\r\n          $pcop_not_required =  explode(\',\', $this->request->post[\'options_pcop_not_required\']);\r\n          \r\n          foreach ($product_options as &$product_option) {\r\n            if ( in_array($product_option[\'product_option_id\'], $pcop_not_required) ) {\r\n              $product_option[\'required\'] = false;\r\n							unset($this->request->post[\'copu_product_id\'][$product_option[\'product_option_id\']]); // custom file upload\r\n            }\r\n          }\r\n          unset($product_option);\r\n        }\r\n        \r\n        // >> Parent-child Options\r\n        \r\n      ]]></add>\r\n    </operation>\r\n  </file>\r\n  \r\n</modification>', 1, '2018-03-19 20:11:20');
INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES
(11, 14, 'LIVEOPENCART: Product Option Image PRO / Изображения опций PRO', 'liveopencart.poip', 'LIVEOPENCART (support@liveopencart.com / help@liveopencart.ru)', '3.0.2', 'http://liveopencart.com', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n	<code>liveopencart.poip</code>\r\n  <name>LIVEOPENCART: Product Option Image PRO / Изображения опций PRO</name>\r\n  <version>3.0.2</version>\r\n  <author>LIVEOPENCART (support@liveopencart.com / help@liveopencart.ru)</author>\r\n  <link>http://liveopencart.com</link>\r\n  \r\n	<file path=\"admin/controller/catalog/option.php\" error=\"skip\">\r\n		\r\n    <!-- MODULE INSTALLED FLAG & MODULE ENTRIES-->\r\n    <operation >\r\n      <search><![CDATA[\r\n				function getForm()\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				\r\n				$data = array_merge( isset($data) ? $data : array(), $this->liveopencart_poip->getModel()->getOptionPageData() ) ;\r\n\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n  </file>\r\n	\r\n  <file path=\"admin/model/catalog/option.php\" error=\"skip\">\r\n  \r\n    <operation>\r\n      <search><![CDATA[\r\n				$this->db->query(\"DELETE FROM \" . DB_PREFIX . \"option_description\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				$this->liveopencart_poip->getModel()->deleteRealOptionSettings($option_id);\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation>\r\n      <search><![CDATA[\r\n				if (isset($data[\'option_value\'])) {\r\n      ]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				if ( isset($data[\'poip_settings\']) ) {\r\n					$this->liveopencart_poip->getModel()->setRealOptionSettings($option_id, $data[\'poip_settings\']);\r\n				}\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n		\r\n  </file>\r\n  \r\n  <file path=\"admin/view/template/catalog/option_form.twig\">\r\n    \r\n    <operation >\r\n      <search><![CDATA[\r\n				<table id=\"option-value\" class=\"table table-striped table-bordered table-hover\">\r\n			]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n				<!-- Product Option Image PRO module << -->\r\n				{% if poip_installed ?? false %}\r\n				\r\n					<div class=\"form-group\">\r\n						<label class=\"col-sm-2 control-label\" for=\"input-sort-order\">{{ poip_module_name }}:</label>\r\n						<div class=\"col-sm-10\">\r\n							<div class=\"row\">\r\n						\r\n								{% for poip_setting in poip_settings_details %}\r\n									<div class=\"col-sm-4\">\r\n									{{ poip_setting.title }}:\r\n									<select name=\"poip_settings[{{ poip_setting.name }}]\" class=\"form-control\">\r\n										{% if poip_setting.values %}\r\n											<option value=\"0\">{{ poip_settings_enable_disable_options[0] }}</option>\r\n											{% for val_id, val_text in poip_setting.values %}\r\n												<option value=\"{{ 1+val_id }}\" \r\n													{% if (poip_saved_settings[poip_setting.name] ?? 0) == (val_id+1) %}\r\n														selected\r\n													{% endif %}\r\n												>{{ val_text }}</option>\r\n											{% endfor %}\r\n										{% else %}\r\n											{% for poip_settings_enable_disable_option_value,poip_settings_enable_disable_option_name in poip_settings_enable_disable_options %}\r\n												<option value=\"{{ poip_settings_enable_disable_option_value }}\"\r\n													{% if (poip_saved_settings[poip_setting.name] ?? 0) == poip_settings_enable_disable_option_value %}\r\n														selected\r\n													{% endif %}\r\n												>{{ poip_settings_enable_disable_option_name }}</option>\r\n											{% endfor %}\r\n										{% endif %}\r\n									</select>\r\n									</div>\r\n								{% endfor %}\r\n								{#\r\n								<?php foreach ($poip_settings_names as $poip_setting_name) { ?>\r\n									<div class=\"col-sm-4\">\r\n									<?php echo ${\"entry_\".$poip_setting_name}; ?>:\r\n									<select name=\"poip_settings[<?php echo $poip_setting_name; ?>]\" class=\"form-control\">\r\n										<?php\r\n											$poip_html = \"\";\r\n											\r\n											// new\r\n											if (in_array(\'entry_\'.$poip_setting_name.\"_v0\", $poip_settings_values)) {\r\n												$poip_html .= \"<option value=\\\"0\\\" \".(isset($poip_saved_settings[$poip_setting_name]) && $poip_saved_settings[$poip_setting_name]==0 ? \"selected\":\"\").\">\".$poip_settings_select_options[0].\"</option>\";\r\n												for ($i=1; $i<9; $i++) {\r\n													if (in_array(\'entry_\'.$poip_setting_name.\"_v\".$i, $poip_settings_values)) {\r\n														$poip_html .= \"<option value=\\\"\".(1+$i).\"\\\" \".(isset($poip_saved_settings[$poip_setting_name]) && $poip_saved_settings[$poip_setting_name]==(1+$i) ? \"selected\":\"\").\">\".${(\"entry_\".$poip_setting_name.\"_v\".$i)}.\"</option>\";\r\n													}\r\n												}\r\n												$poip_html .= \"<option value=\\\"\".(1).\"\\\" \".(isset($poip_saved_settings[$poip_setting_name]) && $poip_saved_settings[$poip_setting_name]==1 ? \"selected\":\"\").\">\".${(\"entry_\".$poip_setting_name.\"_v0\")}.\"</option>\";\r\n											} else {\r\n												foreach ($poip_settings_select_options as $select_value => $select_name) {\r\n													$poip_html .= \"<option value=\\\"\".$select_value.\"\\\" \".(isset($poip_saved_settings[$poip_setting_name]) && $poip_saved_settings[$poip_setting_name]==$select_value ? \"selected\":\"\").\">\".$select_name.\"</option>\";\r\n												}\r\n											}\r\n											echo $poip_html;\r\n										?>\r\n										\r\n									</select>\r\n									</div>\r\n								<?php } ?>\r\n								#}\r\n						\r\n							</div>	\r\n						</div>\r\n					</div>\r\n				\r\n				{% endif %}\r\n				<!-- >> Product Option Image PRO module -->\r\n			]]></add>\r\n    </operation>\r\n    \r\n  </file>\r\n  \r\n  \r\n	<file path=\"admin/controller/catalog/product.php\" error=\"skip\">\r\n    \r\n		<operation >\r\n      <search><![CDATA[\r\n				protected function getForm() {\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				\r\n				$this->liveopencart_poip->getModel()->addProductPageResources();\r\n				\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n		\r\n		\r\n		<!-- MODULE INSTALLED FLAG & MODULE ENTRYES-->\r\n    <operation >\r\n      <search><![CDATA[\r\n				function getForm()\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				\r\n				$data = array_merge( isset($data) ? $data : array(), $this->liveopencart_poip->getModel()->getProductPageData() );\r\n				\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <!-- OPTIONS IMAGES TO TPL -->\r\n    <operation >\r\n      <search><![CDATA[\r\n				$product_option_value_data[] = array(\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				\'images\' => (isset($product_option_value[\'images\'])? $this->liveopencart_poip->getModel()->addThumbsIfNeeded($product_option_value[\'images\']) : array()),\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n		<!-- // new -->\r\n		<operation error=\"log\">\r\n      <search><![CDATA[\r\n				$data[\'product_images\'][] = array(\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// << Product Option Image PRO module\r\n				\'poip\'      => isset($product_image[\'poip\']) ? $product_image[\'poip\'] : false,\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n  </file>\r\n	\r\n	<file path=\"admin/model/catalog/product.php\" error=\"skip\">\r\n    \r\n    <!-- ADD & EDIT PRODUCT - DELETE OPTIONS IMAGES & OPTIONS IMAGE SETINGS-->\r\n    <operation >\r\n      <search><![CDATA[\r\n				$this->db->query(\"DELETE FROM \" . DB_PREFIX . \"product_option WHERE product_id\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				$this->liveopencart_poip->getModel()->deleteProductOptionValueImages($product_id);\r\n				$this->liveopencart_poip->getModel()->deleteRealProductSettings($product_id);\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <!-- ADD & EDIT PRODUCT - SAVE OPTIONS IMAGES -->\r\n    <operation >\r\n      <search><![CDATA[\r\n				$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_option_value\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				$product_option_value_id = $this->db->getLastId();\r\n\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n\r\n				if (isset($product_option_value[\'images\'])) {\r\n					$this->liveopencart_poip->getModel()->saveProductOptionValueImages($product_id, $product_option_id, $product_option_value_id, $product_option_value[\'images\']);\r\n				}\r\n				\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <!-- ADD & EDIT PRODUCT - SAVE PRODUCT OPTIONS IMAGE SETTINGS -->\r\n    <operation >\r\n      <search><![CDATA[\r\n				$product_option_id = $this->db->getLastId();\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				if ( isset($product_option[\'poip_settings\']) ) {\r\n					$this->liveopencart_poip->getModel()->setRealProductSettings($product_id, $product_option_id, $product_option[\'poip_settings\']);\r\n				}\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <!-- GET PRODUCT OPTIONS WITH IMAGES & MODULE SETTINGS-->\r\n    <operation>\r\n      <search><![CDATA[\r\n				public function getProductOptions($product_id\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				\r\n				$images 								= $this->liveopencart_poip->getModel()->getProductOptionImages($product_id);\r\n				$product_poip_settings 	= $this->liveopencart_poip->getModel()->getRealProductSettings($product_id);\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation >\r\n      <search><![CDATA[\r\n				$product_option_value_data[] = array(\r\n      ]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				$option_value_images = array();\r\n				if (isset($images[$product_option[\'product_option_id\']][$product_option_value[\'product_option_value_id\']])) {\r\n					foreach ($images[$product_option[\'product_option_id\']][$product_option_value[\'product_option_value_id\']] as $poip_image) {\r\n						$option_value_images[] = $poip_image;\r\n					}\r\n				}\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation >\r\n      <search><![CDATA[\r\n				$product_option_value_data[] = array(\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				\'images\' => $option_value_images,\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation >\r\n      <search><![CDATA[\r\n				$product_option_data[] = array(\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				\'poip_settings\' => (isset($product_poip_settings[$product_option[\'product_option_id\']]) ? $product_poip_settings[$product_option[\'product_option_id\']] : false),\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n    \r\n		<operation >\r\n			<search><![CDATA[\r\n				$query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"product_image WHERE product_id = \'\"\r\n      ]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				\r\n				$query->rows = $this->liveopencart_poip->getModel()->addOptionsDataToAdditionalImages( $product_id, $query->rows );\r\n				\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n  \r\n		<operation >\r\n      <search><![CDATA[\r\n	      if (isset($data[\'product_image\'])) {\r\n      ]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n				// Product Option Image PRO module <<\r\n				\r\n				if ( isset($data[\'product_image\']) && (__FUNCTION__ == \'addProduct\' || __FUNCTION__ == \'editProduct\') ) {\r\n					liveopencart\\poip::initLibrary($this->registry);\r\n					$this->liveopencart_poip->getModel()->saveOptionsForImages($product_id, $data[\'product_image\']);\r\n				}\r\n				\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n		\r\n  </file>\r\n  \r\n  <file path=\"admin/view/template/catalog/product_form.twig\">\r\n    \r\n		<!-- << OPTIONS FOR IMAGES STYLE // new -->\r\n		\r\n		<operation >\r\n      <search><![CDATA[\r\n				<td class=\"text-right\">{{ entry_sort_order }}</td>\r\n      ]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n				<!-- Product Option Image PRO module << -->\r\n				{% if (poip_installed ?? false) and (poip_global_settings[\'options_images_edit\'] ?? 0) %}\r\n						<td class=\"text-left\">{{ entry_option }}</td>\r\n				{% endif %}\r\n				<!-- >> Product Option Image PRO module -->\r\n			]]></add>\r\n    </operation>\r\n		 \r\n		<operation error=\"skip\">\r\n      <search><![CDATA[\r\n				<td class=\"text-left\"><button type=\"button\" onclick=\"addImage()\r\n      ]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n				<!-- Product Option Image PRO module << -->\r\n				{% if (poip_installed ?? false) and (poip_global_settings[\'options_images_edit\'] ?? 0) %}\r\n						<td class=\"text-left\"></td>\r\n				{% endif %}\r\n				<!-- >> Product Option Image PRO module -->\r\n			]]></add>\r\n    </operation>\r\n		\r\n		<operation >\r\n      <search ><![CDATA[\r\n				<div id=\"content\">\r\n			]]></search>\r\n      <add position=\"after\"><![CDATA[\r\n				<!-- Product Option Image PRO module << -->\r\n				{% if (poip_installed ?? false) %}\r\n				{# {% if (poip_installed ?? false) and ( (poip_global_settings[\'options_images_edit\'] ?? 0) == 1 ) %} #}\r\n					\r\n					<script type=\"text/javascript\"><!--\r\n						$().ready(function(){\r\n							poip.init({\r\n								product_options 								: {{ (product_options ?? false)|json_encode }},\r\n								option_values 									: {{ (option_values ?? false)|json_encode }},\r\n								texts 													: {{ (poip_texts ?? false)|json_encode }},\r\n								settings_details 								: {{ (poip_settings_details ?? false)|json_encode }},\r\n								settings_enable_disable_options : {{ (poip_settings_enable_disable_options ?? false)|json_encode }},\r\n								saved_settings									: {{ poip_saved_settings|json_encode }},\r\n								image_placeholder 							: \'{{ placeholder }}\',\r\n							});\r\n						});\r\n					//--></script>\r\n						\r\n				{% endif %}\r\n				<!-- >> Product Option Image PRO module -->\r\n			]]></add>\r\n    </operation>\r\n		\r\n		<operation >\r\n      <search><![CDATA[\r\n				{% set image_row = image_row + 1 %}\r\n      ]]></search>\r\n      <ad