e/*/template/journal2/module/custom_sections_product.twig||catalog/view/theme/*/template/journal2/module/super_filter_product.twig\" error=\"skip\">  \r\n    \r\n    <operation error=\"skip\">\r\n      <search regex=\"true\"><![CDATA[~(src=\"{{ product.thumb }}\")~]]></search>\r\n      <add><![CDATA[$1 data-poip-product-id=\"{{ product[\'product_id\'] }}\" {% if product.poip_images ?? false %} data-poip-images=\"{{ product.poip_images|json_encode|e }}\" {% endif %} ]]></add>\r\n    </operation>\r\n    \r\n	</file>\r\n\r\n	<file path=\"catalog/controller/checkout/cart.php\" error=\"skip\">  \r\n    \r\n    <operation error=\"skip\">\r\n      <search ><![CDATA[\r\n				$json[\'image\'] = Journal2Utils::resizeImage($this->model_tool_image, $product_info[\'image\']\r\n      ]]></search>\r\n      <add position=\"before\"><![CDATA[\r\n				// << Product Option Image PRO module\r\n				liveopencart\\poip::initLibrary($this->registry);\r\n				$poip_options = array();\r\n				foreach ($option as $po_id => $pov_id) {\r\n					$poip_options[] = array(\'product_option_id\'=>$po_id, \'product_option_value_id\'=>$pov_id);\r\n				}\r\n				$product_info[\'image\'] = $this->liveopencart_poip->getModel()->getProductCartImage((int)$product_info[\'product_id\'], $poip_options, $product_info[\'image\']);\r\n				// >> Product Option Image PRO module\r\n      ]]></add>\r\n    </operation>\r\n		\r\n	</file>\r\n	\r\n</modification>\r\n		', 1, '2018-03-19 20:11:56');
