(17, 20, 'LIVEOPENCART: Live Price PRO / Живая цена PRO', 'liveopencart.liveprice', 'LIVEOPENCART ( support@liveopencart.com / help@liveopencart.ru )', '3.0.5b', 'http://liveopencart.com', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<modification>\r\n	<code>liveopencart.liveprice</code>\r\n	<name>LIVEOPENCART: Live Price PRO / Живая цена PRO</name>\r\n	<version>3.0.5b</version>\r\n	<author>LIVEOPENCART ( support@liveopencart.com / help@liveopencart.ru )</author>\r\n	<link>http://liveopencart.com</link>\r\n\r\n	<!-- starting from -->\r\n	<file path=\"catalog/controller/product/{product,category,manufacturer,compare,search}.php|catalog/controller/extension/module/{bestseller,featured,latest,special}.php\">\r\n		\r\n	  <operation error=\"skip\"> \r\n			<search regex=\"true\"><![CDATA[~(\\$data\\[\'products\'\\]|\\$this->data\\[\'products\'\\]|\\$products)\\[(\\$product_id)?\\] = array\\(~]]></search>\r\n			<add><![CDATA[\r\n				// << Live Price\r\n				$this->load->model(\'extension/liveopencart/liveprice\');\r\n				\r\n				if ( isset($result) ) {\r\n					$lp_product = $result;\r\n				} else {\r\n					$lp_product = $product_info;\r\n				}\r\n				\r\n				$prices = $this->model_extension_liveopencart_liveprice->getPriceStartingFrom( $lp_product, $price, $special, isset($tax) ? $tax : false );\r\n				if ( $prices ) {\r\n					$price = $prices[\'f_price\'];\r\n					if ($prices[\'f_special\']) {\r\n						$special = $prices[\'f_special\'];\r\n					}\r\n					if ( isset($tax) ) {\r\n						$tax = $prices[\'f_tax\'];\r\n					}\r\n				}\r\n				// >> Live Price\r\n				\r\n				$1[$2] = array(\r\n			]]></add>\r\n		</operation>\r\n    \r\n  </file>\r\n	\r\n	<file path=\"admin/controller/catalog/product.php\">\r\n		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$this->load->language(\'catalog/product\');]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n			// << Live Price\r\n			\r\n			$this->load->language(\'extension/module/liveprice\', \'liveopencart.liveprice\');\r\n			\r\n			// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[function getForm()]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			// << Live Price\r\n			\r\n			$this->load->model(\'extension/module/liveprice\');\r\n			\r\n			$data[\'liveprice_installed\'] = $this->model_extension_module_liveprice->installed();\r\n			$data[\'liveprice_all_customers_groups\'] = $this->language->get(\'liveopencart.liveprice\')->get(\'liveprice_all_customers_groups\');\r\n			\r\n			// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[\'price\'             => $product_discount[\'price\'],]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			// << Live Price\r\n			\'price_prefix\'             => isset($product_discount[\'price_prefix\']) ? $product_discount[\'price_prefix\'] : \'\',\r\n			// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[\'price\'             => $product_special[\'price\'],]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			// << Live Price\r\n			\'price_prefix\'             => isset($product_special[\'price_prefix\']) ? $product_special[\'price_prefix\'] : \'\',\r\n			// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n	</file>\r\n	\r\n	<file path=\"admin/model/catalog/product.php\">\r\n		\r\n		<!-- discounts -->\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_discount]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			// << Live Price\r\n			\r\n			$product_discount_id = $this->db->getLastId();\r\n			if ( isset($product_discount[\'price_prefix\']) ) {\r\n				$this->load->model(\'extension/module/liveprice\');\r\n				if ( $this->model_extension_module_liveprice->installed() ) {\r\n					$this->db->query(\"UPDATE \".DB_PREFIX.\"product_discount SET price_prefix=\'\".$this->db->escape($product_discount[\'price_prefix\']).\"\' WHERE product_discount_id = \".(int)$product_discount_id.\" \");\r\n				}\r\n			}\r\n			\r\n			// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<!-- specials -->\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_special]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			// << Live Price\r\n			\r\n			$product_special_id = $this->db->getLastId();\r\n			if ( isset($product_special[\'price_prefix\']) ) {\r\n				$this->load->model(\'extension/module/liveprice\');\r\n				if ( $this->model_extension_module_liveprice->installed() ) {\r\n					$this->db->query(\"UPDATE \".DB_PREFIX.\"product_special SET price_prefix=\'\".$this->db->escape($product_special[\'price_prefix\']).\"\' WHERE product_special_id = \".(int)$product_special_id.\" \");\r\n				}\r\n			}\r\n			\r\n			// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n	</file>\r\n	\r\n	<file path=\"admin/view/template/catalog/product_form.twig\" error=\"skip\">\r\n		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[{% if product_option_value.price_prefix == \'-\' %}]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				<!-- << Live Price -->\r\n				\r\n				{% if liveprice_installed ?? 0 %}\r\n					<option value=\"+%\" {{ (product_option_value[\'price_prefix\']==\'%\' or product_option_value[\'price_prefix\']==\'+%\') ? \'selected\' : \'\' }}>+%</option>\r\n					<option value=\"-%\" {{ product_option_value[\'price_prefix\']==\'-%\' ? \'selected\' : \'\' }}>&ndash;%</option>\r\n					<option value=\"=%\" {{ product_option_value[\'price_prefix\']==\'=%\' ? \'selected\' : \'\' }}>=%</option>\r\n					<option value=\"*\" {{ product_option_value[\'price_prefix\']==\'*\' ? \'selected\' : \'\' }}>*</option>\r\n					<option value=\"/\" {{ product_option_value[\'price_prefix\']==\'/\' ? \'selected\' : \'\' }}>/</option>\r\n					<option value=\"=\" {{ product_option_value[\'price_prefix\']==\'=\' ? \'selected\' : \'\' }}>=</option>\r\n				{% endif %}\r\n				\r\n				<!-- >> Live Price -->\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[$(\'#option-value\' + option_row + \' tbody\').append(html);]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				// << Live Price\r\n				{% if liveprice_installed ?? 0 %}\r\n					var ppm_option = $(\'#option-value-row\'+option_value_row).find(\'select[name*=price_prefix]\').find(\'option[value=\"-\"]\');\r\n					ppm_option.before(\'<option value=\"+%\">+%</option>\');\r\n					ppm_option.before(\'<option value=\"-%\">&ndash;%</option>\');\r\n					ppm_option.before(\'<option value=\"=%\">=%</option>\');\r\n					ppm_option.before(\'<option value=\"*\">*</option>\');\r\n					ppm_option.before(\'<option value=\"/\">/</option>\');\r\n					ppm_option.before(\'<option value=\"=\">=</option>\');\r\n				{% endif %}				\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<!-- discounts -->\r\n		<operation error=\"skip\">\r\n			<search regex=\"true\"><![CDATA[~(<input type=\"text\" name=\"product_discount\\[{{ discount_row }}\\]\\[price\\]\")~]]></search>\r\n			<add><![CDATA[\r\n				<!-- << Live Price -->\r\n				{% if liveprice_installed ?? 0 %}\r\n					<select name=\"product_discount[{{ discount_row }}][price_prefix]\" class=\"form-control\">\r\n						<option value=\"=\"\r\n							{% if (product_discount[\'price_prefix\'] ?? \'\') == \'=\' %}\r\n								selected\"\r\n							{% endif %}\r\n						>=</option>\r\n						<option value=\"_\"\r\n							{% if (product_discount[\'price_prefix\'] ?? \'\') == \'_\' %}\r\n								selected\r\n							{% endif %}\r\n							>= %</option>\r\n						<option value=\"%\"\r\n							{% if ( product_discount[\'price_prefix\'] ?? \'\') == \'%\' %}\r\n								selected\r\n							{% endif %}\r\n						>&ndash; %</option>\r\n					</select>\r\n				{% endif %}\r\n				\r\n				<!-- >> Live Price -->\r\n				$1\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<!-- discounts -->\r\n		<operation error=\"skip\">\r\n			<search regex=\"true\"><![CDATA[~(<input type=\"text\" name=\"product_discount\\[\' \\+ discount_row \\+ \'\\]\\[price\\]\")~]]></search>\r\n			<add><![CDATA[\';\r\n				// << Live Price\r\n				{% if liveprice_installed ?? 0 %}\r\n					html += \'<select name=\"product_discount[\' + discount_row + \'][price_prefix]\" class=\"form-control\">\';\r\n					html += \'	<option value=\"=\" >=</option>\';\r\n					html += \'	<option value=\"_\" >= %</option>\';\r\n					html += \'	<option value=\"%\" >&ndash; %</option>\';\r\n					html += \'</select>\';\r\n				{% endif %}\r\n				// >> Live Price\r\n				html += \'$1\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<!-- specials -->\r\n		<operation error=\"skip\">\r\n			<search regex=\"true\"><![CDATA[~(<input type=\"text\" name=\"product_special\\[{{ special_row }}\\]\\[price\\]\")~]]></search>\r\n			<add><![CDATA[\r\n				<!-- << Live Price -->\r\n				{% if liveprice_installed ?? 0 %}\r\n					<select name=\"product_special[{{ special_row }}][price_prefix]\" class=\"form-control\">\r\n						<option value=\"=\" {{ ((product_special[\'price_prefix\'] ?? \'\') == \'=\' ) ? \'selected\' : \'\' }} >=</option>\r\n						<option value=\"_\" {{ ((product_special[\'price_prefix\'] ?? \'\') == \'_\' ) ? \'selected\' : \'\' }} >= %</option>\r\n						<option value=\"%\" {{ ((product_special[\'price_prefix\'] ?? \'\') == \'%\' ) ? \'selected\' : \'\' }} >&ndash; %</option>\r\n					</select>\r\n				{% endif %}\r\n				<!-- >> Live Price -->\r\n				$1\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<!-- specials -->\r\n		<operation error=\"skip\">\r\n			<search regex=\"true\"><![CDATA[~(<input type=\"text\" name=\"product_special\\[\' \\+ special_row \\+ \'\\]\\[price\\]\")~]]></search>\r\n			<add><![CDATA[\'; // <--- !!!\r\n				// << Live Price\r\n				{% if liveprice_installed ?? 0 %}\r\n					html += \'<select name=\"product_special[\' + special_row + \'][price_prefix]\" class=\"form-control\">\';\r\n					html += \'	<option value=\"=\" >=</option>\';\r\n					html += \'	<option value=\"_\" >= %</option>\';\r\n					html += \'	<option value=\"%\" >&ndash; %</option>\';\r\n					html += \'</select>\';\r\n				{% endif %}\r\n				// >> Live Price\r\n				html += \'$1\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<!-- all customers groups discount -->\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[<td class=\"text-left\"><select name=\"product_discount[{{ discount_row }}][customer_group_id]\" class=\"form-control\">]]></search>\r\n			<add position=\"after\" ><![CDATA[\r\n				<!-- << Live Price -->\r\n				{% if liveprice_installed ?? 0 %}\r\n					<option value=\"-1\">{{ liveprice_all_customers_groups }}</option>\r\n				{% endif %}\r\n				<!-- >> Live Price -->\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<!-- all customers groups discount -->\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[html += \'  <td class=\"text-left\"><select name=\"product_discount[\' + discount_row + \'][customer_group_id]\" class=\"form-control\">\';]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n			// << Live Price\r\n			{% if liveprice_installed ?? 0 %}\r\n				html += \'<option value=\"-1\">{{ liveprice_all_customers_groups }}</option>\';\r\n			{% endif %}	\r\n			// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<!-- all customers groups special -->\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[<td class=\"text-left\"><select name=\"product_special[{{ special_row }}][customer_group_id]\" class=\"form-control\">]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				<!-- << Live Price -->\r\n				<option value=\"-1\">{{ liveprice_all_customers_groups }}</option>\r\n			\r\n				<!-- >> Live Price -->\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<!-- all customers groups special -->\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[html += \'  <td class=\"text-left\"><select name=\"product_special[\' + special_row + \'][customer_group_id]\" class=\"form-control\">\';]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				// << Live Price\r\n				html += \'<option value=\"-1\">{{ liveprice_all_customers_groups }}</option>\';\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n	</file>\r\n	\r\n	\r\n	<!-- calc option once -->\r\n	<file path=\"admin/controller/catalog/option.php\">\r\n		\r\n		<operation error=\'skip\'>\r\n			<search><![CDATA[$this->load->language(\'catalog/option\');]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				// << Live Price\r\n				$this->load->language(\'extension/module/liveprice\');\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<operation>\r\n			<search><![CDATA[protected function getForm() {]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				// << Live Price\r\n				$data[\'entry_calculate_once\'] 			= $this->language->get(\'entry_calculate_once\');\r\n				$data[\'entry_calculate_once_help\'] 	= $this->language->get(\'entry_calculate_once_help\');\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<operation>\r\n			<search><![CDATA[if (isset($this->request->post[\'option_value\'])) {]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				// << Live Price\r\n				if (isset($this->request->post[\'calculate_once\'])) {\r\n					$data[\'calculate_once\'] = $this->request->post[\'calculate_once\'];\r\n				} elseif (!empty($option_info) && isset($option_info[\'calculate_once\']) ) {\r\n					$data[\'calculate_once\'] = $option_info[\'calculate_once\'];\r\n				} else {\r\n					$data[\'calculate_once\'] = \'\';\r\n				}\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n	</file>\r\n	\r\n	<!-- calc option once -->\r\n	<file path=\"admin/model/catalog/option.php\">\r\n		\r\n		<operation>\r\n			<search><![CDATA[class ModelCatalogOption extends Model {]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				// << Live Price\r\n				function lp_checkTables() {\r\n					$query = $this->db->query(\"SHOW COLUMNS FROM `\" . DB_PREFIX . \"option` WHERE field=\'calculate_once\' \");\r\n					if (!$query->num_rows) {\r\n						$this->db->query(\"ALTER TABLE `\".DB_PREFIX.\"option` ADD COLUMN `calculate_once` tinyint(1) NOT NULL \" );\r\n					}\r\n				}\r\n				function lp_saveData($option_id, $data) {\r\n					$this->lp_checkTables();\r\n					$this->db->query(\"UPDATE `\" . DB_PREFIX . \"option`\r\n														SET calculate_once = \'\" . (isset($data[\'calculate_once\']) ? (int)$data[\'calculate_once\'] : 0) . \"\'\r\n														WHERE option_id = \'\" . (int)$option_id . \"\'\r\n														\");\r\n				}\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>	\r\n		\r\n		<operation>\r\n			<search><![CDATA[$option_id = $this->db->getLastId();]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				// << Live Price\r\n				$this-> lp_saveData($option_id, $data);\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<operation>\r\n			<search><![CDATA[public function editOption(]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				// << Live Price\r\n				$this-> lp_saveData($option_id, $data);\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n	\r\n	</file>\r\n	\r\n	\r\n	<!-- calc option once -->\r\n	<file path=\"admin/view/template/catalog/option_form.twig\">\r\n		\r\n		<operation>\r\n			<search><![CDATA[<input type=\"text\" name=\"sort_order\" value=\"{{ sort_order }}\" placeholder=\"{{ entry_sort_order }}\" id=\"input-sort-order\" class=\"form-control\" />]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				<!-- << Live Price -->\r\n					</div>\r\n				</div>\r\n				<div class=\"form-group\">\r\n					<label class=\"col-sm-2 control-label\" for=\"input-calculate-once\">\r\n						<span data-toggle=\"tooltip\" title=\"{{ entry_calculate_once_help }}\">\r\n							{{ entry_calculate_once }}\r\n						</span>	\r\n					</label>\r\n					<div class=\"col-sm-10\">\r\n						<div class=\"checkbox\">\r\n							<label>\r\n								<input type=\"ch