n					$lp_quantity 			= $cart[\'quantity\'];\r\n					$lp_price_data 		= $this->getLivePriceModel()->getProductPrice( $lp_product_id, -$lp_quantity, $lp_options, $lp_recurring_id, false, true, true );\r\n					\r\n					$price = $lp_price_data[\'prices\'][\'price\'];\r\n					$option_price = $lp_price_data[\'prices\'][\'option_price\'];\r\n					$option_data = $lp_price_data[\'option_data\'];\r\n				}\r\n				\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n	</file>\r\n	\r\n	\r\n	<file path=\"catalog/controller/product/product.php\">\r\n		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[\r\n				public function index() {\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				// << Live Price\r\n				\r\n				$this->load->model(\'extension/liveopencart/liveprice\');\r\n				$lp_data = $this->model_extension_liveopencart_liveprice->getProductPageAdditionalData();\r\n				$data = array_merge( !empty($data) ? $data : array(), $lp_data);\r\n				\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[\r\n				$price = $this->currency->format($this->tax->calculate($option_value[\'price\'], $product_info[\'tax_class_id\'], $this->config->get(\'config\r\n			]]></search>\r\n			<add position=\"after\"><![CDATA[\r\n				// << Live Price\r\n\r\n				$this->load->model(\'extension/liveopencart/liveprice\');\r\n				$price = $this->model_extension_liveopencart_liveprice->changeOptionPriceFormat($price, $option_value);\r\n\r\n				// >> Live Price\r\n			]]></add>\r\n		</operation>\r\n		\r\n		<operation error=\"skip\">\r\n			<search><![CDATA[\r\n				foreach ($discounts as $discount) {\r\n			]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				// << Live Price\r\n				\r\n				// display discounts with prefixes when percent discount enabled for total amount\r\n				$this->load->model(\'extension/liveopencart/liveprice\');\r\n				if ( !($data[\'discounts\'] = $this->model_extension_liveopencart_livepri