INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`) VALUES ('2', '2', 'Parent-child Options', 'parent-child-options', 'LIVEOPENCART (support@liveopencart.com)', '3.0.0', 'http://liveopencart.com', '<?xml version="1.0" encoding="utf-8"?>\r\n<modification>\r\n\t<code>parent-child-options</code>\r\n  <name>Parent-child Options</name>\r\n  <version>3.0.0</version>\r\n  <author>LIVEOPENCART (support@liveopencart.com)</author>\r\n  <link>http://liveopencart.com</link>\r\n  \r\n  <file path="admin/controller/catalog/product.php">\r\n\t\t\r\n\t\t<operation>\r\n      <search><![CDATA[\r\n\t\t\t\t$this->load->language(\'catalog/product\');\r\n\t\t\t]]></search>\r\n      <add position="before"><![CDATA[\r\n        // << Parent-child Options\r\n\t\t\t\t$this->load->language(\'extension/module/parent_child_options\');\r\n        // >> Parent-child Options\r\n      ]]></add>\r\n    </operation>\r\n    \r\n    <operation>\r\n      <search>\r\n        <![CDATA[function getForm() {]]>\r\n      </search>\r\n      <add position="after"><![CDATA[\r\n        // << Parent-child Options\r\n        \r\n\t\t\t\t$this->load->model(\'extension/module/parent_child_options\');\r\n\t\t\t\tif ( $this->model_extension_module_parent_child_options->installed() ) {\r\n\t\t\t\t\r\n\t\t\t\t\t$this->document->addScript($this->model_extension_module_parent_child_options->getProductEditPageScriptPath());\r\n\t\t\t\t\r\n\t\t\t\t\t// Create a new language container \r\n\t\t\t\t\t$pcop_language = new Language($this->config->get(\'config_language\'));\r\n\t\t\t\t\t$pcop_language->load(\'extension/module/parent_child_options\');\r\n\t\t\t\t\r\n\t\t\t\t\t$data[\'pcop_installed\']\t\t\t\t\t\t\t\t\t\t= true;\r\n\t\t\t\t\t$data[\'pcop_entry_settings\']              = $pcop_language->get(\'pcop_entry_settings\');\r\n\t\t\t\t\t$data[\'pcop_entry_no_parent_options\']     = $pcop_language->get(\'pcop_entry_no_parent_optio